import loadScript from '../util/loadScript';
import getEnabler from '../util/getEnabler';

export default function untilEnablerIsInitialized() {

  let prom = Promise.resolve();

  if (!window.Enabler) {
    prom = prom.then(() => loadScript('https://s0.2mdn.net/ads/studio/Enabler.js'));
  }

  prom = prom.then(() => getEnabler())

  prom = prom.then(Enabler => {
      return new Promise(resolve => {
        if (!Enabler.isInitialized()) {
          Enabler.addEventListener(studio.events.StudioEvent.INIT, resolve);
        } else {
          resolve();
        }
      });
    });

  return prom;
}

/**
 *
 */
export default class Disposable {
  /**
   *
   * @param {string} name
   * @param {Function} func
   * @param {EventDispatcher} dispatcher
   */
  constructor(name, func, dispatcher) {
    this._name = name;
    this._func = func;
    this._dispatcher = dispatcher;
  }

  dispose() {
    this._dispatcher.removeEventListener(this._name, this._func);
    this._dispatcher = this._name = this._func = null;
  }
}

/**
 *
 */
import Disposable from './Disposable';

export default class EventDispatcher {
  _events = {};

  /**
   *
   * @param {string} name
   * @param {Array<any>>} args
   */
  dispatchEvent(name, ...args) {
    if (this._events[name]) {
      const events = this._events[name];
      const { length } = events;
      for (let i = 0; i < length; i += 1) {
        const func = events[i];
        func.apply(this, args);
      }
    }
  }

  /**
   *
   * @param {string} name
   * @param {function} func
   */
  addEventListener(name, func) {
    if (!this._events[name]) {
      this._events[name] = [];
    }

    this._events[name].push(func);

    return new Disposable(name, func, this);
  }

  /**
   *
   * @param {string} event
   * @param {func} func
   */
  removeEventListener(event, func) {
    if (!func || typeof func !== 'function') {
      throw new Error('second argument is required and needs to be a function');
    }

    if (this._events[event]) {
      this._events[event] = this._events[event].filter(value => value !== func);
    }
  }
}

/**
 * @example
 * Browser.isMobile
 *
 * @type {{
 *  isMobile: boolean
 *  isOS: boolean
 *  isOS9up: boolean
 *  isPad: boolean
 *  isSafari: boolean
 *  isFirefox: boolean
 *  isChrome: boolean
 *  isEdge: boolean
 *  isOpera: boolean
 * }}
 *
 * @return {object}
 */

const userAgent = navigator.userAgent;
let Browser = {};

Browser.isMobile = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(userAgent);
Browser.isOS = /iPad|iPhone|iPod/.test(userAgent);
Browser.isOS9up = Browser.isOS && navigator.appVersion.match(/OS (\d+)_(\d+)_?(\d+)?/)[1] > 9;
Browser.isPad = /iPad/.test(userAgent);
Browser.isSafari = /^((?!chrome|android).)*safari/i.test(userAgent);
Browser.isFirefox = /firefox|fxios/i.test(userAgent);
Browser.isChrome = /chrome|chromium|crios/i.test(userAgent);
Browser.isEdge = /edg/i.test(userAgent);
Browser.isOpera = /opr\//i.test(userAgent);

export default Browser;

/**
 * @description
 *
 *   const raf = new Raf((time, diff) => {
 *     // do animation diff is the time difference between this frame and the previous frame
 *   }).start()
 *
 *   // stop animation.
 *   raf.stop();
 */
export default class PhysicsSafeRaf {
  rafNum = -1;
  currentTime = 0;

  /**
   *
   * @param {Function} callback
   * @param limitFps
   */
  constructor(callback, limitFps = -1) {
    this.callback = callback;
    this.limitFps = limitFps;

    this.hasLimiter = limitFps > 0;
    this.limitMsps = 1000 / limitFps;
    this.timeAcc = this.limitMsps;
  }

  tick = timestamp => {
    this.rafNum = window.requestAnimationFrame(this.tick);
    const timeDiff = timestamp - this.currentTime;
    this.currentTime = timestamp;

    if (timeDiff > 0 && !this.hasLimiter) {
      this.callback.call(null, timestamp, timeDiff);
    } else if (timeDiff > 0) {
      this.timeAcc += timeDiff;
      while (this.timeAcc > this.limitMsps) {
        this.timeAcc -= this.limitMsps;
        this.callback.call(null, timestamp, this.limitMsps);
      }
    }
  };

  valueOf = () => this.rafNum;
  start = () => {
    if (this.rafNum === -1) {
      this.tick();
    }

    return this;
  };
  stop = () => {
    cancelAnimationFrame(this.rafNum);
    this.rafNum = -1;

    return this;
  };
}

