import getGlobalStyleSheet from './getGlobalStyleSheet';

/**
 * Programmatically create a css style rule.
 *
 * @param {String} classSelector Class, ID or element selector.
 * @param {String} rules CSS rules.
 */
export default function createStyle(classSelector, rules) {
  classSelector = classSelector !== 'banner' ? `#banner ${classSelector}` : classSelector;

  const styleSheet = getGlobalStyleSheet();

  if (styleSheet.sheet.insertRule) {
    styleSheet.sheet.insertRule(`${classSelector}{${rules}}`, 0);
  } else if (styleSheet.styleSheet) {
    styleSheet.styleSheet.addRule(classSelector, rules);
  } else if (styleSheet.sheet) {
    styleSheet.sheet.addRule(classSelector, rules);
  }
}

import makeObservable from './makeObservable';

function getObject(str) {
  let [type, path] = str.split(':');

  type = type.trim();
  path = path.trim().split('.');

  return {
    type,
    path,
  };
}

function getValue(model, path) {
  path.forEach(item => {
    if (model.hasOwnProperty(item)) {
      model = model[item];
    }
  });

  if (typeof model !== 'string') {
    throw new Error(`defined path is wrong ${path.join('.')}`);
  }

  return model;
}

/**
 *
 * @param {object} model
 * @param {HTMLElement} element
 * @param {boolean} isObservable
 *
 * @example
 * data-bind="html: text" , applies html to a element from the property text.
 * data-bind="src: logo.Url" , applies src to a element from the property logo.Url.
 * data-bind="href: logo.Url" , applies href to a element from the property logo.Url.
 * data-bind="style.backgroundColor: bgcolor" , applies style.backgroundColor to a element from the property bgcolor.
 */
function dataBind(model, element, isObservable = false) {
  const elements = element.querySelectorAll('[data-bind]');

  if (isObservable) {
    let { observable: model, dispatcher } = makeObservable(model);

    elements.forEach(el => {
      const data = getObject(el.getAttribute('data-bind'));

      dispatcher.addEventListener(data.path.join('.'), () =>
        updateElement(data.type, getValue(model, data.path), el),
      );
    });

    return { model, dispatcher };
  } else {
    elements.forEach(el => {
      const data = getObject(el.getAttribute('data-bind'));

      updateElement(data.type, getValue(model, data.path), el);
    });
  }

  return { model };
}

function updateElement(type, val, element) {
  switch (type) {
    case 'text': {
      element.innerText = val;
      break;
    }

    case 'html': {
      element.innerHTML = val;
      break;
    }

    case 'href':
    case 'src': {
      element[type] = val;
      break;
    }

    default: {
      // match anything with style.*
      const match = /style\.([\w-]+)/.exec(type);
      if (match) {
        element.style[match[1]] = val;
      } else {
        element.setAttribute(type, val);
      }

      break;
    }
  }
}

export default dataBind;

import getStyleRuleValue from './getStyleRuleValue';

const basicElementsToSearch = ['div', 'span', 'img', 'canvas', 'svg', 'circle', 'path'];

/**
 *
 * @param {HTMLElement} element
 * @param {Array<string>} styles
 * @param {string} sheet
 * @param {object} obj
 * @return {{all: Array}}
 */
export default function findElementByCSS(
  element,
  styles = null,
  customTypes = null,
  sheet = null,
  obj = {
    all: [],
  },
) {
  const elementsToSearch = basicElementsToSearch.concat(customTypes);

  if (element && element.childNodes && element.childNodes.length > 0) {
    const children = Array.from(element.querySelectorAll('*'));
    children.forEach(child => {
      if (
        child.type === 'image/svg+xml' ||
        elementsToSearch.indexOf(child.nodeName.toLowerCase()) !== -1
      ) {
        if (child.id || child.className) {
          if (styles) {
            styles = typeof styles === 'string' ? [styles] : styles;

            styles.forEach(style => {
              if (!obj[style]) {
                obj[style] = [];
              }

              if (child.id && obj[style].indexOf(child) === -1) {
                const val = getStyleRuleValue(`.${style}`, `#${child.id}`, sheet);
                if (val) {
                  obj[style].push(child);
                }
              }

              const cssClasses =
                typeof child.className === 'object'
                  ? String(child.className.baseVal).split(' ')
                  : String(child.className).split(' ');

              cssClasses.forEach(cssClass => {
                if (cssClass && obj[style].indexOf(child) === -1) {
                  const val = getStyleRuleValue(`.${style}`, `.${cssClass}`, sheet);
                  if (val && (`.${cssClass}` === val || cssClass === val)) {
                    obj[style].push(child);
                  }
                }
              });
            });

            obj.all.push(child);
            findElementByCSS(child, styles, customTypes, sheet, obj);
          } else {
            obj.push(child);
            findElementByCSS(child, null, null, null, obj);
          }
        }
      }
    });
  }

  return obj;
}

import findElementByCSS from './findElementByCSS';

export default function findElementsByCSS(element, styles, customTypes, sheet) {
  let obj;
  if (styles) {
    obj = findElementByCSS(element, styles, customTypes, sheet);
  } else {
    obj = findElementByCSS(element);
  }

  return obj;
}

/**
 * @param {Array<HTMLElement>} copyElements
 *
 * @example
 *  const title = document.body.querySelector('.title');
 *  const ctaCopy = document.body.querySelector('.cta_copy');
 *  fitText([title, ctaCopy]);
 *
 */

export default function fitText(...copyElements) {
  copyElements = copyElements.flat();

  copyElements.forEach(copyElement => {
    if (window.gsap) {
      window.gsap.set(copyElement, { clearProps: 'fontSize, lineHeight' });
    }

    const { parentElement } = copyElement;
    let fontSize = Number(
      window
        .getComputedStyle(parentElement, null)
        .getPropertyValue('font-size')
        .replace('px', ''),
    );
    let lineHeight =
      Number(
        window
          .getComputedStyle(parentElement, null)
          .getPropertyValue('line-height')
          .replace('px', ''),
      ) || fontSize + 1;
    const targetWidth = Number(
      window
        .getComputedStyle(copyElement, null)
        .getPropertyValue('width')
        .replace('px', ''),
    );
    const parentWidth = Number(
      window
        .getComputedStyle(parentElement, null)
        .getPropertyValue('width')
        .replace('px', ''),
    );
    const targetHeight = Number(
      window
        .getComputedStyle(copyElement, null)
        .getPropertyValue('height')
        .replace('px', ''),
    );
    const parentHeight = Number(
      window
        .getComputedStyle(parentElement, null)
        .getPropertyValue('height')
        .replace('px', ''),
    );

    if (targetHeight > parentHeight || targetWidth > parentWidth) {
      while (
        Number(
          window
            .getComputedStyle(copyElement, null)
            .getPropertyValue('height')
            .replace('px', ''),
        ) > parentHeight ||
        Number(
          window
            .getComputedStyle(copyElement, null)
            .getPropertyValue('width')
            .replace('px', ''),
        ) > parentWidth
      ) {
        if (fontSize < 1 || lineHeight < 1) {
          return;
        }
        fontSize -= 0.2;
        lineHeight -= 0.2;
        copyElement.style.fontSize = `${fontSize}px`;
        copyElement.style.lineHeight = `${lineHeight}px`;
      }
    }
  });
}

