let prom = null;

/**
 * @return {Promise<unknown>}
 */
export default function getEnabler() {
  if (window.Enabler) {
    prom = Promise.resolve(window.Enabler);
  }

  if (!prom) {
    prom = new Promise((resolve, reject) => {
      const limit = 100;
      let count = 0;

      const checkLoop = function() {
        if (count > limit) {
          reject(new Error('Enabler not found'));
        }
        if (window.Enabler) {
          resolve(window.Enabler);
        } else {
          count++;
          setTimeout(checkLoop, 100);
        }
      };

      checkLoop();
    });
  }

  return prom;
}

let _canvas = null;
function getCanvas() {
  if (!_canvas) {
    _canvas = document.createElement('canvas');
  }
  return _canvas;
}

/**
 * Returns the font size ideal for the indicated with.
 *
 * @param {string} text copy
 * @param {number} maxFontSizeInPixels Starting max font size
 * @param {string} fontFamily font family that the copy is using.
 * @param {number} width width that the text needs to fit in.
 */
export default function getFontSizeForText(text, maxFontSizeInPixels, fontFamily, width) {
  const ctx = getCanvas().getContext('2d');

  // if(!fontTest.test(font)){
  //   throw new Error('font string not valid');
  // }

  let isValid = false;
  while (!isValid) {
    ctx.font = `${maxFontSizeInPixels}px ${fontFamily}`;
    if (ctx.measureText(text).width < width) {
      isValid = true;
    } else {
      // eslint-disable-next-line no-plusplus
      maxFontSizeInPixels--;
    }
  }

  return maxFontSizeInPixels;
}

/**
 *
 * @param v
 * @return {string|boolean}
 */
export default function getQueryVar(v) {
  const q = window.location.search.substring(1);
  const vs = q.split('&');
  for (let i = 0; i < vs.length; i++) {
    const p = vs[i].split('=');
    if (p[0] === v) {
      return p[1];
    }
  }
  return false;
}

import loadText from './loadText';

/**
 *
 * @param {HTMLElement} container
 * @return {Promise<void>}
 */
function inlineSvg(container) {
  let elements = Array.from(container.querySelectorAll('img[svg]'));
  elements = [...elements, ...Array.from(container.querySelectorAll('img[inline]'))];

  elements.forEach(img => {
    if (!img.src) {
      throw new Error('img element with missing src attribute.');
    }
  });

  return Promise.all(elements.map(img => loadText(img.src))).then(svgList => {
    svgList.forEach((svgText, index) => {
      const el = elements[index];
      const { id, className } = el;
      const div = document.createElement('div');
      const parent = el.parentNode;

      parent.appendChild(div);
      div.innerHTML = svgList[index];
      div.firstChild.id = id;
      div.firstChild.setAttribute('class', className);

      parent.replaceChild(div.firstChild, el);
      parent.removeChild(div);
    });
  });
}

export default inlineSvg;

import undefinedOrNull from './undefinedOrNull';

/**
 * Wait for a DOM element to load.
 *
 * @param {string} selector
 * @param {number} time
 *
 * @example
 *  isElementLoaded('svg.logo').then((elm) => elm);
 *
 *  isElementLoaded('.containerHidden', 2000).then((elm) => elm);
 *
 * @returns {Promise<HTMLImageElement>}
 */
export default function isElementLoaded(selector, time = 500) {
  if (undefinedOrNull(selector)) {
    return null;
  }

  // eslint-disable-next-line compat/compat
  return new Promise((resolve, reject) => {
    if (typeof selector === 'undefined' || selector === null) {
      return reject(null);
    }

    let _interval = setInterval(() => {
      if (document.querySelector(selector) != null) {
        clearInterval(_interval);
        return resolve(document.querySelector(selector));
      }
    }, time);
  });
}

import loadScript from './loadScript';
import loadImage from './loadImage';
import loadText from './loadText';

const regJson = /\.json$/;
// const regText = /\.(html|text|md|MD)$/;
const regJs = /\.js$/;
const regBitmap = /\.(png|gif|jpg|jpeg|svg)$/;

export default function load(url) {
  switch (true) {
    case regJson.test(url): {
      return loadScript(url);
    }

    case regJs.test(url): {
      return loadScript(url);
    }

    case regBitmap.test(url): {
      return loadImage(url);
    }

    default: {
      // case regText.test(url): {
      return loadText(url);
    }
  }
}

import load from './load';

/**
 * Loads multiple files and output an array with the objects
 * @param {Array<string>} urls
 * @param {boolean} sequential
 * @param {function} loader
 *
 * @example
 *  loadAll(['./my_example_image.png', './my_example_image2.png', './my_example_image3.png'], true);
 *
 *  loadAll(['./custom_image.png']);
 *
 * @return {Promise<Array>}
 */
export default function loadAll(urls, sequential = false, loader = load) {
  if (sequential) {
    return urls.reduce((prom, url) => prom.then(() => loader(url)), Promise.resolve(true));
  }

  return Promise.all(urls.map(url => loader(url)));
}

/**
 * Loads an image and returns the image element
 *
 * @example
 *  loadImage('./my_example_image.png');
 *
 * @param {string} src
 * @return {Promise<HTMLImageElement>}
 */
export default function loadImage(src) {
  return new Promise((resolve, reject) => {
    const img = document.createElement('img');
    img.onload = function() {
      resolve(this);
    };
    img.onerror = reject;
    img.dataset.src = src;
    img.src = src;
  });
}

/**
 * Load a JSON file.
 * @param {String} url
 *
 * @example
 *  loadJSON('https://jsonplaceholder.typicode.com/todos/1');
 *
 * @return {Promise<any>}
 */
export default function loadJSON(url) {
  return fetch(url).then(response => response.json());
}

/**
 * Load a JavaScript file.
 *
 * @param {String} url Script file url. A string array of url's also possible.
 * @param {Object} [opt] Callback function when file loaded.
 *
 * @example
 *  loadScript('https://cdnjs.cloudflare.com/ajax/libs/gsap/3.9.1/gsap.min.js')
 *    .then((file) => console.log(file)
 *  );
 *
 * @retun {Promise}
 */
export default function loadScript(url, opts = {}) {
  return new Promise((resolve, reject) => {
    const head = document.head || document.querySelector('head');
    const script = document.createElement('script');
    script.type = opts.type || 'text/javascript';
    script.charset = opts.charset || 'utf8';
    script.async = true;
    script.onload = function() {
      this.onerror = this.onload = null;
      resolve(script);
    };

    script.onerror = function() {
      this.onerror = this.onload = null;
      reject(new Error(`Failed to load ${this.src}`), script);
    };

    script.src = url;

    head.appendChild(script);
  });
}

import loadScript from './loadScript';
import loadAll from './loadAll';

/**
 * Loads multiple files and output an array with the objects
 * @param {Array<string>} urls
 * @param {boolean} sequential
 *
 * @example
 *  loadScriptAll(['./mycustomScript.js', './mycustomScript2.js', './mycustomScript3.js'], true);
 *
 *  loadScriptAll(['./mycustomScript.png']);
 *
 * @return {Promise<Array>}
 */
export default function loadScriptAll(urls, sequential = false) {
  return loadAll(urls, sequential, loadScript);
}

/**
 * Load text from a source
 * @param {string} src
 *
 * @example
 *  loadText('./my_example_data.html');
 *
 * @return {Promise<string>}
 */
export default function loadText(src) {
  return fetch(src).then(response => response.text());
}

import traverseObject from './traverseObject';
import EventDispatcher from '../event/EventDispatcher';

/**
 *
 * @param obj
 * @returns {{observable: any, dispatcher: EventDispatcher}}
 */
function makeObservable(obj) {
  const observable = JSON.parse(JSON.stringify(obj));
  const dispatcher = new EventDispatcher();

  traverseObject(observable, (leafValue, leafName, source, traversedPath) => {
    Object.defineProperty(source, leafName, {
      value: leafName,
      set: function(v) {
        const oldValue = this[`@private__${leafName}`];
        this[`@private__${leafName}`] = v;
        dispatcher.dispatchEvent(traversedPath.join('.'), v, oldValue);
      },
      get: function() {
        return this[`@private__${leafName}`];
      },
    });
  });

  return { observable, dispatcher };
}

export default makeObservable;

import loadImage from './loadImage';

/**
 * Will add canvas elements to the wrapper that are masked by the png images
 * @param {array} config
 *
 * @example
 *  masker([
 *      ['./bg.jpg', './bg_transparent_white_small.png'],
 *      ['./bg.jpg', './flame_mask_small.png'],
 *      ['./bg.jpg', './girl_mask_small.png']
 *   ], document.body.querySelectorAll('.wrapper'));
 *
 * @param {HTMLElement} wrapper
 * @return {Promise<unknown>}
 */
export default function masker(config, wrapper) {
  return new Promise(function(resolve) {
    const images = config
      .reduce(function(acc, curr) {
        acc.push(...curr);
        return acc;
      }, [])
      .filter(function(value, index, self) {
        return self.indexOf(value) === index;
      });

    Promise.all(images.map(loadImage))
      .then(images =>
        config.map(items => items.map(item => images.find(img => img.dataset.src === item))),
      )
      .then(mapped => {
        mapped = mapped.map(([img, mask]) => {
          const canvas = document.createElement('canvas');
          let width = (canvas.width = img.width);
          let height = (canvas.height = img.height);

          const ctx = canvas.getContext('2d');
          ctx.drawImage(img, 0, 0);
          ctx.globalCompositeOperation = 'destination-in';
          ctx.drawImage(mask, 0, 0, width, height);

          return canvas;
        });

        mapped.forEach(canvas => wrapper.appendChild(canvas));
        return mapped;
      })
      .then(resolve);
  });
}

import loadAll from './loadAll';

/**
 * Will search for all child elements with a attribute data-src and will load it change it to .src,
 * @param {HTMLElement} element
 *
 * @example
 *  politeLoadImages(document.querySelector('svg'))
 *
 * @return {Promise<void>}
 */
export default async function politeLoadImages(element) {
  const imageList = Array.from(element.querySelectorAll('img[data-src]'));
  const imageUrlList = imageList.map(img => img.getAttribute('data-src'));
  const result = await loadAll(imageUrlList);

  result.forEach((img, index) => {
    imageList[index].src = img.src;
  });

  while (result.length) {
    result.pop();
  }
}

/**
 * Get random number
 * @param {number} min
 * @param {number} max
 *
 * @example
 *  rand(10, 420);
 *
 * @return {number}
 */
export default function rand(min, max) {
  return Math.floor(Math.random() * (max - min + 1) + min);
}

/**
 * Convert the RGB to hex code
 *
 * @param {number} r
 * @param {number} g
 * @param {number} b
 *
 * @example
 *  rgbToHex(0, 51, 255)
 *
 * @returns {string>}
 */
export default function rgbToHex(r, g, b) {
  return '#' + ((1 << 24) + (r << 16) + (g << 8) + b).toString(16).slice(1);
}

/**
 * Strip any HTML elements in the string with the help of DOMParser
 *
 * @param {string} html
 *
 * @example
 *  stripHtml('<h1>Hey, <strong>nice banners</strong>!</h1>')
 *
 * @returns {string>}
 */
export default function stripHtml(html) {
  return new DOMParser().parseFromString(html, 'text/html').body.textContent || '';
}

/**
 * Will travel through a object and when it finds a leaf will call the function from the second argument
 *
 * @param {object} source
 * @param {function<leafValue: any, leafName: any, source: object, currPath: Array:string> => void} fn
 * @param {number} maxNesting
 * @param {Array<string>} path
 */
function traverseObject(source, fn, maxNesting = 10, path = []) {
  if (maxNesting < 0) {
    return;
  }

  for (var propName in source) {
    const traversedPath = [...path];
    if (source.hasOwnProperty(propName)) {
      traversedPath.push(propName);
      if (typeof source[propName] === 'object') {
        traverseObject(source[propName], fn, maxNesting - 1, traversedPath);
      } else {
        fn(source[propName], propName, source, traversedPath);
      }
    }
  }
}

export default traverseObject;

/**
 * Checks whether a given variable is undefined or null
 * @param obj
 * @returns {boolean}
 */
export default function undefinedOrNull(obj) {
  return typeof obj === 'undefined' || obj === null;
}

/**
 * Wait until N time
 * @param {number} time
 *
 * @example
 *  until(5000);
 *
 * @return {Promise}
 */
export default function until(time) {
  return new Promise(resolve => setTimeout(resolve, time));
}

/**
 * Return id
 *
 * @example
 *  uuid();
 *
 * @return {string}
 */
let _uuid = new Date().getTime();
export default function uuid() {
  return (_uuid++).toString(16);
}
